import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

log.info " This is my first program"

//

log.info context.expand ('${#TestCase#name}')
log.info context.expand ('${#TestCase#age}')

log.info testRunner.testCase.testSuite.testCases["E2E"].getPropertyValue("age")

testRunner.testCase.testSuite.testCases["E2E"].setPropertyValue("age", "1234")

log.info testRunner.testCase.getPropertyValue("Dept")

log.info testRunner.testCase.testSuite.testCases["GetDetails"].testSteps["get"].getPropertyValue("Request")

def project = testRunner.testCase.testSuite.project
log.info project.getPropertyValue("id")

def addReq = testRunner.testCase.testSuite.testCases["E2E"].testSteps["addEmp"].getPropertyValue("Request")

def name = testRunner.testCase.testSuite.testCases["E2E"].getPropertyValue("name")
def age = testRunner.testCase.testSuite.testCases["E2E"].getPropertyValue("age")
def id = testRunner.testCase.testSuite.testCases["E2E"].getPropertyValue("id")
def dept = testRunner.testCase.testSuite.testCases["E2E"].getPropertyValue("Department")

def xmlAdd = new XmlHolder(addReq) //ability to update the XML file

xmlAdd.setNodeValue("//typ:addEmployee/typ:name", name)
xmlAdd.setNodeValue("//typ:addEmployee/typ:id", id)
xmlAdd.setNodeValue("//typ:addEmployee/typ:age", age) 
xmlAdd.setNodeValue("//typ:addEmployee/typ:Department",dept)
def newAddXML = xmlAdd.getXml();

testRunner.testCase.testSuite.testCases["E2E"].testSteps["addEmp"].setPropertyValue("Request", newAddXML)

//Call Soap Request

def addTestStep= testRunner.testCase.testSuite.testCases["E2E"].testSteps["addEmp"]

def contextAddEmployee = new WsdlTestRunContext(addTestStep);

addTestStep.run(testRunner, contextAddEmployee)
